// src/components/Home.tsx
import React, { useEffect, useState } from 'react';
import { Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext.tsx'; 

const Home: React.FC = () => {
  const { isAuthenticated, setIsAuthenticated, setUser, user } = useAuth(); 
  const navigate = useNavigate();
  const [timer, setTimer] = useState(60); 
  let timeout: ReturnType<typeof setTimeout>;

  // Function to handle logout
  const handleLogout = () => {
    setIsAuthenticated(false); 
    setUser(null); 
    navigate('/signin'); 
  };


  const resetSessionTimer = () => {
    if (isAuthenticated) {
      clearTimeout(timeout); 


      setTimer(60);
      timeout = setTimeout(handleLogout, 60000); // Set a new timeout (60 seconds)

      // Update the timer every second
      const countdown = setInterval(() => {
        setTimer((prevTimer) => {
          if (prevTimer === 1) {
            clearInterval(countdown); // Clear the interval when the timer reaches 0
          }
          return prevTimer - 1;
        });
      }, 1000); // Update the timer every second
    }
  };

  // Listen for user activity events to reset the session timer
  useEffect(() => {
    if (!isAuthenticated) return; // Do nothing if the user is not authenticated

    // Reset session timer on user activity
    window.addEventListener('mousemove', resetSessionTimer);
    window.addEventListener('keydown', resetSessionTimer);

    // Start the timer when the component mounts
    resetSessionTimer();

    // Clean up listeners when the component unmounts or when isAuthenticated changes
    return () => {
      window.removeEventListener('mousemove', resetSessionTimer);
      window.removeEventListener('keydown', resetSessionTimer);
      clearTimeout(timeout); // Clear the timeout if the component is unmounted
    };
  }, [isAuthenticated]); // Only apply if the user is authenticated

  // Redirect to sign-in page if the user is not authenticated
  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/signin'); // Redirect to the sign-in page
    }
  }, [isAuthenticated, navigate]);

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#f8f9fa' }}>
      <div style={{ textAlign: 'center' }}>
        <h1>Welcome to the Home Page!</h1>
        {/* Display user's name if available */}
        {user ? (
          <p>Hello, {user.name}!</p>
        ) : (
          <p>You are successfully signed in.</p>
        )}

        <p>Time remaining: {timer} seconds</p> {/* Show the timer on the UI */}

        <Button variant="danger" onClick={handleLogout}>
          Logout
        </Button>
      </div>
    </div>
  );
};

export default Home;
