import React, { useEffect, useState, useRef } from 'react';
import { Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext.tsx';

const Home: React.FC = () => {
  const { isAuthenticated, setIsAuthenticated, setUser, user } = useAuth();
  const navigate = useNavigate();
  const [timer, setTimer] = useState(10);
  const timeoutRef = useRef<ReturnType<typeof setTimeout> | null>(null);
  const intervalRef = useRef<ReturnType<typeof setInterval> | null>(null);

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUser(null);
    navigate('/signin');
  };

  const resetSessionTimer = () => {
    if (isAuthenticated) {
      // Clear any previous timeout or interval
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }

      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }

      // Reset the timer to 60 seconds
      setTimer(60);

      // Set a timeout for logging out after 60 seconds
      timeoutRef.current = setTimeout(handleLogout, 60000);

      // Set an interval to update the timer every second
      intervalRef.current = setInterval(() => {
        setTimer((prevTimer) => {
          if (prevTimer === 1) {
            clearInterval(intervalRef.current as NodeJS.Timeout); // Clear the interval when the timer reaches 0
          }
          return prevTimer - 1;
        });
      }, 1000);
    }
  };

  useEffect(() => {
    if (!isAuthenticated) return;

    // Add event listeners for user activity
    window.addEventListener('mousemove', resetSessionTimer);
    window.addEventListener('keydown', resetSessionTimer);

    // Initialize the timer
    resetSessionTimer();

    return () => {
      // Clean up event listeners and timers when the component is unmounted or authentication changes
      window.removeEventListener('mousemove', resetSessionTimer);
      window.removeEventListener('keydown', resetSessionTimer);

      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }

      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isAuthenticated]);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/signin');
    }
  }, [isAuthenticated, navigate]);

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#f8f9fa' }}>
      <div style={{ textAlign: 'center' }}>
        <h1>Welcome to the Home Page!</h1>
        {user ? (
          <p>Hello, {user.name}!</p>
        ) : (
          <p>You are successfully signed in.</p>
        )}
        <p>Time remaining: {timer} seconds</p>
        <Button variant="danger" onClick={handleLogout}>
          Logout
        </Button>
      </div>
    </div>
  );
};

export default Home;
